package strings;

public class String_split {

	public static void main(String[] args)  {
		// TODO Auto-generated method stub
		String a = "levitca";
		System.out.println(a);
		String level[]=a.split(""); // l,e,v,i
		for(String tech:level) {
			System.out.println(tech);
		}
		
		}

	}


