package myfirst_codeOperotors;

public class Invertedoperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = -200;
		int b =  200;
		boolean c = true;
		System.out.println(! c);// ! denotes not
		System.out.println(~a);// (-ve)value then the value will become +ve by decreasing 1 number
		System.out.println(~b);//~(+ve) value then the value will become -ve by decreasing 1 number
		
	}
		

	

}
